import UIKit
import IGListKit

enum Main {
    
    // MARK: - Use cases
  
    enum CurrentWeaher {
        struct Request {
            let latitude: Double
            let longitude: Double
        }
        
        struct Response {
            var weather: WeatherModel?
            var errorDescription: String?
        }
    
        struct ViewModel {
            class DisplayedWeather: ListDiffable {
                var id: Int
                var weather: WeatherModel
                
                // MARK: - Init
                
                init(id: Int, weather: WeatherModel) {
                    self.id = id
                    self.weather = weather
                }
                
                // MARK: - ListDiffable
                
                func diffIdentifier() -> NSObjectProtocol {
                    return id as NSObjectProtocol
                }
                
                func isEqual(toDiffableObject object: ListDiffable?) -> Bool {
                    guard self !== object else { return true }
                    return false
                }
            }
            var displayedWeather: DisplayedWeather
        }
    }
    
    enum Forecast {
        struct Request {
            let latitude: Double
            let longitude: Double
        }
        
        struct Response {
            var forecast: ForecastModel?
            var errorDescription: String?
        }
        
        struct ViewModel {
            class DisplayedForecast: ListDiffable {
                var dayName: String
                var dayNumber: String
                var forecast: [ForecastItem]
                
                // MARK: - Init
                
                init(dayName: String, dayNumber: String, forecast: [ForecastItem]) {
                    self.dayName = dayName
                    self.dayNumber = String(dayNumber.last!)
                    self.forecast = forecast
                }
                
                // MARK: - ListDiffable
                
                func diffIdentifier() -> NSObjectProtocol {
                    return dayName as NSObjectProtocol
                }
                
                func isEqual(toDiffableObject object: ListDiffable?) -> Bool {
                    guard self !== object else { return true }
                    guard let object = object as? DisplayedForecast else { return false }
                    return dayNumber == object.dayNumber
                }
            }
            var displayedForecast: [DisplayedForecast]
        }
    }
}
